    [Email] [nvarchar](max),
    [InceptionDate] [datetime] NOT NULL,
    [InstallmentPayment] [bit] NOT NULL,
    [ClientId] [uniqueidentifier] NOT NULL,
    CONSTRAINT [PK_dbo.Policy] PRIMARY KEY ([ID])
)
